import React, { useEffect, useRef, useState } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

const About = () => {
  const sectionRef = useRef(null);
  const titleRef = useRef(null);
  const cardsRef = useRef(null);
  const backgroundRef = useRef(null);
  const [hoveredCard, setHoveredCard] = useState(null);

  // Enhanced crew data with GTA-style character backstories
  const crewMembers = [
    {
      id: 1,
      name: "ALEX 'TURBO' RODRIGUEZ",
      role: "Crew Leader",
      specialty: "Engine Architecture",
      avatar: "üèéÔ∏è",
      stats: { respect: 98, skill: 95, experience: 92 },
      bio: "Former street racer turned engineering mastermind. Built his first turbocharged engine at 16 and never looked back. Known throughout Los Santos for creating the fastest quarter-mile times in SAE history.",
      backstory: "Grew up in the underground racing scene of East Los Santos. After a near-fatal crash, he channeled his need for speed into engineering excellence, founding SAE Auto Empire.",
      achievements: ["Los Santos Speed King 2022", "Built the 'Turbo Phoenix' - 1200HP Beast", "SAE Founder & Visionary"],
      expertise: ["Turbocharging Systems", "ECU Programming", "Drag Racing Builds"],
      characterArt: `<svg viewBox="0 0 300 400" xmlns="http://www.w3.org/2000/svg">
        <defs>
          <linearGradient id="alexGrad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#39ff14;stop-opacity:0.9" />
            <stop offset="50%" style="stop-color:#f9c74f;stop-opacity:0.7" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
        </defs>
        <!-- Character silhouette with details -->
        <path d="M150 40 C140 45, 135 55, 140 70 L145 90 C142 100, 138 110, 135 130 L130 150 C128 170, 125 190, 130 210 L135 250 C138 270, 142 290, 145 310 L148 350 C150 370, 152 390, 150 400 L145 390 C135 385, 125 380, 120 370 L115 350 C110 330, 105 310, 110 290 L115 270 C118 250, 120 230, 125 210 L130 190 C132 170, 135 150, 138 130 L142 110 C145 90, 148 70, 145 50 C150 40, 155 35, 150 40 Z" fill="url(#alexGrad)"/>
        <!-- Turbo symbol -->
        <circle cx="150" cy="120" r="15" fill="#39ff14" opacity="0.8"/>
        <path d="M145 115 L155 115 L150 125 Z" fill="#000"/>
      </svg>`
    },
    {
      id: 2,
      name: "MAYA 'CIRCUIT' CHEN",
      role: "Tech Specialist",
      specialty: "Electronics & AI",
      avatar: "‚ö°",
      stats: { respect: 94, skill: 98, experience: 88 },
      bio: "PhD in Electrical Engineering from Los Santos University. Pioneered the first AI-assisted tuning system that revolutionized ECU programming. Her electronic warfare skills are legendary.",
      backstory: "Former tech prodigy who built her first computer at age 10. Joined SAE to bring cutting-edge technology to street racing culture.",
      achievements: ["Created 'Neural Tune' AI System", "Hacked Pentagon Systems (Allegedly)", "LS Tech Innovation Award"],
      expertise: ["Artificial Intelligence", "ECU Hacking", "Autonomous Systems"],
      characterArt: `<svg viewBox="0 0 300 400" xmlns="http://www.w3.org/2000/svg">
        <defs>
          <linearGradient id="mayaGrad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#1b03a3;stop-opacity:0.9" />
            <stop offset="50%" style="stop-color:#ff10f0;stop-opacity:0.7" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
        </defs>
        <path d="M150 35 C145 40, 140 50, 145 65 L150 85 C148 95, 145 105, 142 125 L138 145 C135 165, 132 185, 137 205 L142 245 C145 265, 148 285, 152 305 L155 345 C158 365, 160 385, 158 395 L155 385 C145 380, 135 375, 130 365 L125 345 C120 325, 115 305, 120 285 L125 265 C128 245, 130 225, 135 205 L140 185 C142 165, 145 145, 148 125 L152 105 C155 85, 158 65, 155 45 C160 35, 155 30, 150 35 Z" fill="url(#mayaGrad)"/>
        <!-- Circuit pattern -->
        <rect x="140" y="100" width="20" height="2" fill="#39ff14"/>
        <rect x="149" y="95" width="2" height="10" fill="#39ff14"/>
        <circle cx="145" cy="105" r="2" fill="#ff10f0"/>
        <circle cx="155" cy="105" r="2" fill="#ff10f0"/>
      </svg>`
    },
    {
      id: 3,
      name: "JAKE 'DRIFT' MARTINEZ",
      role: "Handling Expert",
      specialty: "Suspension & Dynamics",
      avatar: "üåÄ",
      stats: { respect: 91, skill: 96, experience: 94 },
      bio: "Professional drift champion with over 50 competition wins. Master of vehicle dynamics and suspension geometry. Can make any car dance through corners like it's on rails.",
      backstory: "Started drifting in parking lots at 15. Rose through the ranks to become Los Santos Drift King before joining SAE to share his knowledge.",
      achievements: ["Los Santos Drift Champion 3x", "Built 'Sideways Samurai' - Perfect Drift Car", "Guinness World Record Holder"],
      expertise: ["Drift Tuning", "Suspension Design", "Corner Dynamics"],
      characterArt: `<svg viewBox="0 0 300 400" xmlns="http://www.w3.org/2000/svg">
        <defs>
          <linearGradient id="jakeGrad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#e63946;stop-opacity:0.9" />
            <stop offset="50%" style="stop-color:#f9c74f;stop-opacity:0.8" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
        </defs>
        <path d="M150 30 C142 35, 138 45, 143 60 L148 80 C145 90, 140 100, 137 120 L133 140 C130 160, 127 180, 132 200 L137 240 C140 260, 143 280, 147 300 L150 340 C153 360, 155 380, 153 390 L150 380 C140 375, 130 370, 125 360 L120 340 C115 320, 110 300, 115 280 L120 260 C123 240, 125 220, 130 200 L135 180 C137 160, 140 140, 143 120 L147 100 C150 80, 153 60, 150 40 C155 30, 150 25, 150 30 Z" fill="url(#jakeGrad)"/>
        <!-- Drift marks -->
        <path d="M135 150 Q150 140 165 150" stroke="#e63946" stroke-width="3" fill="none"/>
        <path d="M135 160 Q150 170 165 160" stroke="#f9c74f" stroke-width="2" fill="none"/>
      </svg>`
    },
    {
      id: 4,
      name: "SARAH 'NITRO' WILLIAMS",
      role: "Power Systems",
      specialty: "Forced Induction",
      avatar: "üí®",
      stats: { respect: 89, skill: 94, experience: 90 },
      bio: "Chemistry PhD turned forced induction specialist. Her nitrous oxide systems are works of art that can add 300+ HP instantly. Known for creating the most reliable power adders in the game.",
      backstory: "Former NASA engineer who got bored with rockets and decided to make cars fly instead. Her nitrous systems are legendary for their precision and power.",
      achievements: ["NASA Engineer (Retired)", "Built 'Nitrous Phoenix' - 2000HP Monster", "Zero Catastrophic Failures Record"],
      expertise: ["Nitrous Systems", "Turbocharging", "Chemical Engineering"],
      characterArt: `<svg viewBox="0 0 300 400" xmlns="http://www.w3.org/2000/svg">
        <defs>
          <linearGradient id="sarahGrad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#ff10f0;stop-opacity:0.9" />
            <stop offset="50%" style="stop-color:#39ff14;stop-opacity:0.7" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
        </defs>
        <path d="M150 32 C145 37, 142 47, 147 62 L152 82 C149 92, 146 102, 143 122 L139 142 C136 162, 133 182, 138 202 L143 242 C146 262, 149 282, 153 302 L156 342 C159 362, 161 382, 159 392 L156 382 C146 377, 136 372, 131 362 L126 342 C121 322, 116 302, 121 282 L126 262 C129 242, 131 222, 136 202 L141 182 C143 162, 146 142, 149 122 L153 102 C156 82, 159 62, 156 42 C161 32, 156 27, 150 32 Z" fill="url(#sarahGrad)"/>
        <!-- Nitrous bottle -->
        <rect x="145" y="120" width="10" height="30" fill="#ff10f0" rx="2"/>
        <circle cx="150" cy="115" r="3" fill="#39ff14"/>
      </svg>`
    },
    {
      id: 5,
      name: "CARLOS 'METAL' GARCIA",
      role: "Fabrication Master",
      specialty: "Custom Manufacturing",
      avatar: "üî®",
      stats: { respect: 96, skill: 92, experience: 98 },
      bio: "Master fabricator with 25+ years of experience. Can create anything from roll cages to custom exhaust manifolds with millimeter precision. His welding skills are considered art.",
      backstory: "Third-generation metalworker who learned the craft from his grandfather. Joined SAE to push the boundaries of automotive fabrication.",
      achievements: ["Master Welder Certification", "Built 'Steel Thunder' - Full Custom Frame", "25+ Years Zero Weld Failures"],
      expertise: ["TIG Welding", "CNC Machining", "Custom Fabrication"],
      characterArt: `<svg viewBox="0 0 300 400" xmlns="http://www.w3.org/2000/svg">
        <defs>
          <linearGradient id="carlosGrad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#f9c74f;stop-opacity:0.9" />
            <stop offset="50%" style="stop-color:#e63946;stop-opacity:0.8" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
        </defs>
        <path d="M150 38 C143 43, 140 53, 145 68 L150 88 C147 98, 143 108, 140 128 L136 148 C133 168, 130 188, 135 208 L140 248 C143 268, 147 288, 150 308 L153 348 C156 368, 158 388, 156 398 L153 388 C143 383, 133 378, 128 368 L123 348 C118 328, 113 308, 118 288 L123 268 C126 248, 128 228, 133 208 L138 188 C140 168, 143 148, 147 128 L150 108 C153 88, 156 68, 153 48 C158 38, 153 33, 150 38 Z" fill="url(#carlosGrad)"/>
        <!-- Hammer and wrench -->
        <rect x="140" y="110" width="20" height="4" fill="#f9c74f"/>
