import React, { useEffect, useRef, useState } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

// Import backgrounds for dynamic switching
import background3 from '../assets/background (3).jpg'; // City skyline sunset
import background5 from '../assets/background (5).jpg'; // Beach reflection

const About = () => {
  const sectionRef = useRef(null);
  const titleRef = useRef(null);
  const cardsRef = useRef(null);
  const backgroundRef = useRef(null);
  const [hoveredCard, setHoveredCard] = useState(null);

  // Enhanced crew data with Vice City aesthetic
  const crewMembers = [
    {
      id: 1,
      name: "ALEX 'TURBO' RODRIGUEZ",
      role: "Crew Leader",
      specialty: "Engine Architecture",
      stats: { respect: 98, skill: 95, experience: 92 },
      bio: "Former street racer turned engineering mastermind. Built his first turbocharged engine at 16 in the backstreets of Los Santos. Known for creating the fastest quarter-mile beasts that dominate Vice City's underground scene.",
      backstory: "Grew up in the neon-lit streets of Vice City's underground racing scene. After a near-fatal crash during a midnight run, he channeled his need for speed into engineering excellence, founding SAE Auto Empire in the heart of Los Santos.",
      achievements: ["Vice City Speed King 2023", "Built the 'Neon Phoenix' - 1500HP Monster", "SAE Empire Founder"],
      expertise: ["Twin-Turbo Systems", "ECU Hacking", "Street Racing Builds"],
      characterArt: `<svg viewBox="0 0 300 400" xmlns="http://www.w3.org/2000/svg">
        <defs>
          <linearGradient id="alexGrad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#ff0080;stop-opacity:0.9" />
            <stop offset="50%" style="stop-color:#00ffff;stop-opacity:0.7" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
          <filter id="neonGlow">
            <feGaussianBlur stdDeviation="3" result="coloredBlur"/>
            <feMerge> 
              <feMergeNode in="coloredBlur"/>
              <feMergeNode in="SourceGraphic"/>
            </feMerge>
          </filter>
        </defs>
        <path d="M150 40 C140 45, 135 55, 140 70 L145 90 C142 100, 138 110, 135 130 L130 150 C128 170, 125 190, 130 210 L135 250 C138 270, 142 290, 145 310 L148 350 C150 370, 152 390, 150 400 L145 390 C135 385, 125 380, 120 370 L115 350 C110 330, 105 310, 110 290 L115 270 C118 250, 120 230, 125 210 L130 190 C132 170, 135 150, 138 130 L142 110 C145 90, 148 70, 145 50 C150 40, 155 35, 150 40 Z" fill="url(#alexGrad)" filter="url(#neonGlow)"/>
        <circle cx="150" cy="120" r="18" fill="#ff0080" opacity="0.8"/>
        <path d="M142 112 L158 112 L150 128 Z" fill="#000"/>
        <rect x="140" y="140" width="20" height="3" fill="#00ffff" opacity="0.8"/>
        <rect x="145" y="160" width="10" height="3" fill="#ff6600" opacity="0.8"/>
      </svg>`,
      neonColor: "#ff0080"
    },
    {
      id: 2,
      name: "MAYA 'CIRCUIT' CHEN",
      role: "Tech Specialist", 
      specialty: "Electronics & AI",
      stats: { respect: 94, skill: 98, experience: 88 },
      bio: "PhD in Electrical Engineering from Los Santos University. Pioneered the first AI-assisted tuning system that revolutionized ECU programming. Her electronic warfare skills earned her legendary status in Vice City's tech underground.",
      backstory: "Former tech prodigy who built supercomputers in her basement at age 12. Joined SAE to bring cutting-edge AI and neural networks to street racing culture, creating the 'Ghost Protocol' - an untraceable ECU system.",
      achievements: ["Created 'Neural Tune' AI System", "Hacked LS Central Bank (Allegedly)", "Vice City Tech Innovation Award"],
      expertise: ["AI Neural Networks", "ECU Ghost Programming", "Quantum Computing"],
      characterArt: `<svg viewBox="0 0 300 400" xmlns="http://www.w3.org/2000/svg">
        <defs>
          <linearGradient id="mayaGrad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#00ffff;stop-opacity:0.9" />
            <stop offset="50%" style="stop-color:#ff0080;stop-opacity:0.7" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
        </defs>
        <path d="M150 35 C145 40, 140 50, 145 65 L150 85 C148 95, 145 105, 142 125 L138 145 C135 165, 132 185, 137 205 L142 245 C145 265, 148 285, 152 305 L155 345 C158 365, 160 385, 158 395 L155 385 C145 380, 135 375, 130 365 L125 345 C120 325, 115 305, 120 285 L125 265 C128 245, 130 225, 135 205 L140 185 C142 165, 145 145, 148 125 L152 105 C155 85, 158 65, 155 45 C160 35, 155 30, 150 35 Z" fill="url(#mayaGrad)"/>
        <rect x="135" y="100" width="30" height="2" fill="#00ffff"/>
        <rect x="149" y="95" width="2" height="15" fill="#00ffff"/>
        <circle cx="140" cy="108" r="3" fill="#ff0080"/>
        <circle cx="160" cy="108" r="3" fill="#ff0080"/>
        <rect x="138" y="120" width="24" height="2" fill="#ff6600"/>
        <rect x="142" y="130" width="16" height="2" fill="#00ffff"/>
      </svg>`,
      neonColor: "#00ffff"
    },
    {
      id: 3,
      name: "JAKE 'DRIFT' MARTINEZ",
      role: "Handling Expert",
      specialty: "Suspension & Drift Dynamics", 
      stats: { respect: 91, skill: 96, experience: 94 },
      bio: "Professional drift champion with over 100 competition wins across Los Santos and Vice City. Master of vehicle dynamics who can make any car dance through corners like a neon-lit ballet. His drift techniques are poetry in motion.",
