// Import the actual GTA V backgrounds
import background1 from '../assets/background (1).jpg'; // Neon diner sunset - MAIN HERO
import background2 from '../assets/background (2).jpg'; // Trevor with palm trees
import background3 from '../assets/background (3).jpg'; // City skyline sunset
import background4 from '../assets/background (4).jpg'; // Street racing sunset
import background5 from '../assets/background (5).jpg'; // Beach reflection sunset

const Hero = () => {
  const heroRef = useRef(null);
  const titleRef = useRef(null);
  const subtitleRef = useRef(null);
  const buttonRef = useRef(null);
  const backgroundRef = useRef(null);
  const loadingRef = useRef(null);
  const hudRef = useRef(null);
  const mapRef = useRef(null);
  const pointerRef = useRef(null);
  const tooltipRef = useRef(null);
  const [isLoading, setIsLoading] = useState(true);
  const [imageLoaded, setImageLoaded] = useState(false);
  const [showTooltip, setShowTooltip] = useState(false);

  useEffect(() => {
    const hero = heroRef.current;
    const title = titleRef.current;
    const subtitle = subtitleRef.current;
    const button = buttonRef.current;
    const background = backgroundRef.current;
    const loading = loadingRef.current;
    const hud = hudRef.current;

    // Preload the main background image
    const img = new Image();
    img.onload = () => setImageLoaded(true);
    img.src = background1;

    // GTA V Style Loading Screen - Authentic
    const loadingTl = gsap.timeline({
      onComplete: () => {
        setTimeout(() => setIsLoading(false), 1000);
      }
    });

    if (imageLoaded) {
      loadingTl
        .to(".loading-bar-fill", { 
          width: "100%", 
          duration: 3,
          ease: "power2.inOut"
        })
        .to(".loading-text", {
          opacity: 0.6,
          duration: 0.5,
          repeat: 5,
          yoyo: true
        }, "-=2")
        .to(loading, {
          opacity: 0,
          duration: 1.5,
          ease: "power2.inOut"
        });
    }

    // Initial setup - everything hidden
    gsap.set([title, subtitle, button, hud, mapRef.current], { opacity: 0 });
    gsap.set(background, { scale: 1.2, opacity: 0 });
    gsap.set(title, { y: 100, rotationX: -90 });
    gsap.set(subtitle, { y: 80, scale: 0.8 });
    gsap.set(button, { y: 60, scale: 0.9 });

    // Cinematic entrance after loading
    const masterTl = gsap.timeline({ delay: 5 });

    // Background dramatic entrance - ensure it shows
    masterTl.to(background, {
      duration: 2.5,
      opacity: 1,
      scale: 1,
      ease: "power2.out"
    });

    // HUD slide in from left (GTA V style)
    masterTl.to(hud, {
      duration: 1,
      opacity: 1,
      x: 0,
      ease: "power3.out"
    }, "-=2");

    // Mini-map entrance from bottom right
    masterTl.to(mapRef.current, {
      duration: 1.5,
      opacity: 1,
      scale: 1,
      y: 0,
      ease: "back.out(1.4)"
    }, "-=1.8");

    // GTA-style title entrance - 3D flip and slide
    masterTl.to(title, {
      duration: 2,
      opacity: 1,
      y: 0,
      rotationX: 0,
      ease: "power3.out"
    }, "-=1.5")
    .to(title, {
      duration: 0.8,
      scale: 1.05,
      ease: "power2.out"
    }, "-=0.5")
    .to(title, {
      duration: 0.6,
      scale: 1,
      ease: "power2.out"
    });

    // Subtitle typewriter effect
    masterTl.to(subtitle, {
      duration: 1.5,
      opacity: 1,
      y: 0,
      scale: 1,
      ease: "power3.out"
    }, "-=1.2");

    // Button glitch entrance
    masterTl.to(button, {
      duration: 1,
      opacity: 1,
      y: 0,
      scale: 1,
      ease: "back.out(1.7)"
    }, "-=0.8")
    .to(button, {
      duration: 0.1,
      scaleX: 1.1,
      repeat: 3,
      yoyo: true,
      ease: "power2.inOut"
    }, "-=0.3");

    // Pointer hover animation
    gsap.to(pointerRef.current, {
      y: -5,
      duration: 1.2,
      ease: "power2.inOut",
      yoyo: true,
      repeat: -1
    });

    // Continuous radar sweep
    gsap.to(".radar-line", {
      rotation: 360,
      duration: 3,
      ease: "none",
      repeat: -1
    });

    // Parallax scroll effect
    const parallaxTl = gsap.timeline({
      scrollTrigger: {
        trigger: hero,
        start: "top top",
        end: "bottom top",
        scrub: 1,
      }
    });

    parallaxTl.to(background, {
      y: -100,
      scale: 1.1,
      ease: "none"
    });

    return () => {
      loadingTl.kill();
      masterTl.kill();
      parallaxTl.kill();
    };
  }, [imageLoaded]);

  const scrollToNext = () => {
    document.querySelector('#about').scrollIntoView({ 
      behavior: 'smooth' 
    });
  };

  return (
    <>
      {/* Authentic GTA V Loading Screen */}
      {isLoading && (
        <div 
          ref={loadingRef}
          className="fixed inset-0 z-50 flex items-center justify-center"
          style={{
            background: 'linear-gradient(135deg, #000000 0%, #1a1a1a 50%, #000000 100%)'
          }}
        >
          <div className="text-center max-w-2xl px-8">
            {/* GTA V Style Logo */}
            <div className="mb-12">
              <h1 className="text-8xl font-black tracking-wider mb-4"
                  style={{
                    fontFamily: 'Impact, Arial Black, sans-serif',
                    color: '#ffffff',
                    textShadow: '3px 3px 0px #000000, -1px -1px 0px #000000, 1px -1px 0px #000000, -1px 1px 0px #000000'
                  }}>
                SAE
              </h1>
              <p className="text-2xl font-bold text-gray-300 tracking-widest"
                 style={{ fontFamily: 'Arial, sans-serif' }}>
                AUTO EMPIRE
              </p>
            </div>

            {/* Loading Bar */}
            <div className="mb-8">
              <div className="w-full h-2 bg-gray-800 rounded-full border border-gray-600">
                <div 
                  className="loading-bar-fill h-full bg-gradient-to-r from-blue-500 to-purple-600 rounded-full"
                  style={{ width: '0%' }}
                ></div>
              </div>
            </div>

            {/* Loading Text */}
            <p className="loading-text text-gray-400 text-lg font-medium"
               style={{ fontFamily: 'Arial, sans-serif' }}>
              Loading Los Santos Auto Empire...
            </p>

            {/* Copyright */}
            <p className="text-gray-600 text-sm mt-8"
               style={{ fontFamily: 'Arial, sans-serif' }}>
              Society of Automotive Engineers © 2024
            </p>
          </div>
        </div>
      )}

      {/* Main Hero Section */}
      <section 
        ref={heroRef}
        className="relative h-screen overflow-hidden"
      >
        {/* ACTUAL Background Image - Your Neon Diner */}
        <div 
          ref={backgroundRef}
          className="absolute inset-0"
          style={{
            backgroundImage: `url(${background1})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center center',
            backgroundRepeat: 'no-repeat',
            opacity: 0,
            transform: 'scale(1.2)'
          }}
        />

        {/* Subtle overlay to enhance readability */}
        <div className="absolute inset-0 bg-gradient-to-t from-black/50 via-black/20 to-transparent" />

        {/* GTA V Style HUD - Top Left */}
        <div 
          ref={hudRef}
          className="absolute top-6 left-6 z-20 opacity-0"
          style={{ transform: 'translateX(-100px)' }}
        >
          <div className="bg-black/70 backdrop-blur-sm rounded-lg p-4 border-l-4 border-blue-500">
            <div className="flex items-center space-x-4 text-white">
              <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center">
                <span className="text-white font-black text-sm">SAE</span>
              </div>
              <div>
                <div className="text-blue-400 font-bold text-sm">LOS SANTOS</div>
                <div className="text-gray-300 text-xs">AUTO EMPIRE</div>
              </div>
            </div>

            <div className="mt-3 pt-3 border-t border-gray-600 flex items-center justify-between">
              <div>
                <div className="text-green-400 font-mono text-lg">$2,547,891</div>
                <div className="text-gray-400 text-xs">TOTAL EARNINGS</div>
              </div>
              <div className="flex space-x-1">
                {[...Array(5)].map((_, i) => (
                  <span key={i} className="text-yellow-400 text-sm">★</span>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="relative z-10 flex flex-col items-center justify-center h-full text-center px-4">

          {/* Clean SAE Title - No Glow */}
          <h1 
            ref={titleRef}
            className="text-9xl md:text-[12rem] lg:text-[16rem] font-black tracking-wider mb-6 opacity-0"
            style={{
              fontFamily: 'Impact, Arial Black, sans-serif',
              color: '#ffffff',
              textShadow: '4px 4px 0px #000000, -2px -2px 0px #000000, 2px -2px 0px #000000, -2px 2px 0px #000000',
              transform: 'translateY(50px)'
            }}
          >
            SAE
          </h1>

          {/* Clean Subtitle */}
          <h2 
            ref={subtitleRef}
            className="text-4xl md:text-6xl lg:text-7xl font-bold tracking-widest mb-8 opacity-0"
            style={{
              fontFamily: 'Arial, sans-serif',
              color: '#ffffff',
              textShadow: '2px 2px 4px rgba(0, 0, 0, 0.8)',
              transform: 'translateY(30px)'
            }}
          >
            AUTO EMPIRE
          </h2>

          {/* Description */}
          <p className="text-xl md:text-2xl text-white mb-12 max-w-4xl leading-relaxed font-medium"
             style={{ 
               textShadow: '1px 1px 2px rgba(0, 0, 0, 0.8)',
               fontFamily: 'Arial, sans-serif'
             }}>
            Welcome to Los Santos' most elite automotive engineering crew. 
            Where cutting-edge technology meets street racing culture in the neon-soaked nights of Vice City.
          </p>

          {/* Clean CTA Button */}
          <button 
            ref={buttonRef}
            onClick={scrollToNext}
            className="px-12 py-4 text-xl font-bold text-white bg-gradient-to-r from-blue-600 to-purple-700 hover:from-blue-700 hover:to-purple-800 rounded-none border-2 border-white/20 transition-all duration-300 hover:scale-105 opacity-0"
            style={{
              fontFamily: 'Arial, sans-serif',
              textShadow: '1px 1px 2px rgba(0, 0, 0, 0.5)',
              transform: 'translateY(20px)',
              clipPath: 'polygon(10px 0%, 100% 0%, calc(100% - 10px) 100%, 0% 100%)'
            }}
          >
            ENTER THE EMPIRE
          </button>
        </div>

        {/* Scroll Indicator */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 z-20">
          <div className="flex flex-col items-center animate-bounce">
            <div className="w-8 h-12 border-2 border-white/60 rounded-full flex justify-center p-2 backdrop-blur-sm">
              <div className="w-1 h-4 bg-white/80 rounded-full animate-pulse"></div>
            </div>
            <span className="text-white/80 text-xs mt-2 font-medium tracking-wider"
                  style={{ fontFamily: 'Arial, sans-serif' }}>
              SCROLL DOWN
            </span>
          </div>
        </div>

        {/* GTA V Style Mini-Map */}
        <div 
          ref={mapRef}
          className="absolute top-6 right-6 z-20 opacity-0"
          style={{ transform: 'translateY(100px) scale(0.8)' }}
        >
          <div className="bg-black/80 backdrop-blur-md rounded-lg p-3 border-2 border-green-400/50 w-48 h-36 relative overflow-hidden">
            {/* Map Background */}
            <div className="absolute inset-0 bg-gradient-to-br from-gray-900 to-black">
              {/* Grid Lines */}
              <div className="absolute inset-0">
                <div className="absolute top-1/2 left-0 w-full h-px bg-green-400/30"></div>
                <div className="absolute top-0 left-1/2 w-px h-full bg-green-400/30"></div>
                {/* Road */}
                <div className="absolute bottom-4 left-0 w-full h-2 bg-gray-600"></div>
                <div className="absolute bottom-4 left-0 w-full h-px bg-yellow-400"></div>
                {/* Buildings */}
                <div className="absolute top-4 left-4 w-8 h-6 bg-gray-700 border border-gray-500"></div>
                <div className="absolute top-2 right-8 w-6 h-8 bg-gray-700 border border-gray-500"></div>
                <div className="absolute bottom-8 left-12 w-10 h-4 bg-gray-700 border border-gray-500"></div>
              </div>

              {/* Radar Sweep */}
              <div className="radar-line absolute top-1/2 left-1/2 origin-left w-16 h-px bg-gradient-to-r from-green-400 to-transparent opacity-60"
                   style={{ transform: 'translate(-50%, -50%)' }}></div>
            </div>

            {/* Location Pointer */}
            <div 
              ref={pointerRef}
              className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 cursor-pointer"
              onMouseEnter={() => setShowTooltip(true)}
              onMouseLeave={() => setShowTooltip(false)}
            >
              <div className="w-4 h-4 bg-red-500 rounded-full border-2 border-white animate-pulse relative">
                <div className="absolute -inset-2 bg-red-500/30 rounded-full animate-ping"></div>
              </div>
            </div>

            {/* Map Label */}
            <div className="absolute bottom-1 right-1 text-xs text-green-400 font-mono font-bold">
              LOS SANTOS
            </div>

            {/* Compass */}
            <div className="absolute top-1 right-1 text-xs text-white font-mono">
              <div className="w-6 h-6 border border-white/50 rounded-full flex items-center justify-center">
                N
              </div>
            </div>
          </div>

          {/* Tooltip */}
          {showTooltip && (
            <div 
              ref={tooltipRef}
              className="absolute -top-12 left-1/2 transform -translate-x-1/2 bg-black/90 backdrop-blur-sm text-white px-3 py-2 rounded border border-green-400/50 whitespace-nowrap text-sm font-medium animate-pulse"
              style={{ fontFamily: 'Arial, sans-serif' }}
            >
              🎯 Welcome! You reached your destination
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-green-400/50"></div>
            </div>
          )}
        </div>

        {/* Bottom fade to black for smooth transition */}
        <div className="absolute bottom-0 left-0 w-full h-32 bg-gradient-to-t from-black to-transparent" />
      </section>
    </>
  );
};

export default Hero;
