import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';

const Hero = () => {
  const heroRef = useRef(null);
  const titleRef = useRef(null);
  const subtitleRef = useRef(null);
  const buttonRef = useRef(null);
  const parallaxRef = useRef(null);
  const cityRef = useRef(null);

  useEffect(() => {
    const hero = heroRef.current;
    const title = titleRef.current;
    const subtitle = subtitleRef.current;
    const button = buttonRef.current;
    const parallax = parallaxRef.current;
    const city = cityRef.current;

    // Initial setup
    gsap.set([title, subtitle, button], { opacity: 0, y: 50 });
    gsap.set(city, { scale: 1.1 });

    // Hero entrance animation
    const tl = gsap.timeline({ delay: 0.5 });

    tl.to(title, { 
      duration: 1.2, 
      opacity: 1, 
      y: 0, 
      ease: "power3.out" 
    })
    .to(subtitle, { 
      duration: 1, 
      opacity: 1, 
      y: 0, 
      ease: "power3.out" 
    }, "-=0.8")
    .to(button, { 
      duration: 0.8, 
      opacity: 1, 
      y: 0, 
      ease: "back.out(1.7)" 
    }, "-=0.6");

    // Parallax effect on scroll
    const parallaxTl = gsap.timeline({
      scrollTrigger: {
        trigger: hero,
        start: "top top",
        end: "bottom top",
        scrub: 1.5,
      }
    });

    parallaxTl.to(city, {
      scale: 1.2,
      y: -100,
      ease: "none"
    });

    parallaxTl.to(parallax, {
      y: -200,
      ease: "none"
    }, 0);

    // Floating animation for subtitle
    gsap.to(subtitle, {
      y: -10,
      duration: 2,
      ease: "power2.inOut",
      yoyo: true,
      repeat: -1
    });

    // Street lights blinking animation
    gsap.to(".street-light", {
      opacity: 0.3,
      duration: 0.5,
      ease: "power2.inOut",
      yoyo: true,
      repeat: -1,
      stagger: {
        each: 0.3,
        from: "random"
      }
    });

    return () => {
      // Cleanup
      tl.kill();
      parallaxTl.kill();
    };
  }, []);

  return (
    <section 
      ref={heroRef}
      className="relative h-screen overflow-hidden bg-gradient-to-b from-gta-black via-gray-900 to-gta-black"
    >
      {/* Background City Skyline */}
      <div 
        ref={cityRef}
        className="absolute inset-0 opacity-30"
        style={{
          backgroundImage: `url("data:image/svg+xml,${encodeURIComponent(`
            <svg viewBox="0 0 1200 600" xmlns="http://www.w3.org/2000/svg">
              <defs>
                <linearGradient id="buildingGrad" x1="0%" y1="0%" x2="0%" y2="100%">
                  <stop offset="0%" style="stop-color:#2a2a2a;stop-opacity:1" />
                  <stop offset="100%" style="stop-color:#0a0a0a;stop-opacity:1" />
                </linearGradient>
              </defs>
              <rect x="0" y="400" width="100" height="200" fill="url(#buildingGrad)"/>
              <rect x="120" y="300" width="80" height="300" fill="url(#buildingGrad)"/>
              <rect x="220" y="350" width="90" height="250" fill="url(#buildingGrad)"/>
              <rect x="330" y="250" width="110" height="350" fill="url(#buildingGrad)"/>
              <rect x="460" y="280" width="85" height="320" fill="url(#buildingGrad)"/>
              <rect x="565" y="200" width="95" height="400" fill="url(#buildingGrad)"/>
              <rect x="680" y="320" width="75" height="280" fill="url(#buildingGrad)"/>
              <rect x="775" y="180" width="120" height="420" fill="url(#buildingGrad)"/>
              <rect x="915" y="290" width="85" height="310" fill="url(#buildingGrad)"/>
              <rect x="1020" y="240" width="100" height="360" fill="url(#buildingGrad)"/>
              <rect x="1140" y="380" width="60" height="220" fill="url(#buildingGrad)"/>
            </svg>
          `)}")`,
          backgroundSize: 'cover',
          backgroundPosition: 'bottom',
          backgroundRepeat: 'no-repeat'
        }}
      />

      {/* Street lights */}
      <div className="absolute bottom-0 left-0 w-full h-full pointer-events-none">
        <div className="street-light absolute bottom-32 left-16 w-2 h-8 bg-gta-yellow opacity-80"></div>
        <div className="street-light absolute bottom-32 left-64 w-2 h-8 bg-gta-yellow opacity-80"></div>
        <div className="street-light absolute bottom-32 right-64 w-2 h-8 bg-gta-yellow opacity-80"></div>
        <div className="street-light absolute bottom-32 right-16 w-2 h-8 bg-gta-yellow opacity-80"></div>
      </div>

      {/* Grid overlay */}
      <div className="absolute inset-0 bg-grid opacity-10"></div>

      {/* Main content */}
      <div 
        ref={parallaxRef}
        className="relative z-10 flex flex-col items-center justify-center h-full text-center px-4"
      >
        {/* SAE Logo/Title */}
        <h1 
          ref={titleRef}
          className="gta-title text-6xl md:text-8xl lg:text-9xl mb-6 neon-text"
        >
          SAE
        </h1>

        {/* Subtitle */}
        <h2 
          ref={subtitleRef}
          className="font-street text-xl md:text-3xl lg:text-4xl mb-4 text-gta-yellow tracking-wider"
        >
          AUTO EMPIRE
        </h2>

        <p className="font-street text-lg md:text-xl text-gray-300 mb-12 max-w-2xl leading-relaxed">
          Welcome to the <span className="neon-pink">Society of Automotive Engineers</span> - 
          Where speed meets engineering excellence in the concrete jungle.
        </p>

        {/* CTA Button */}
        <button 
          ref={buttonRef}
          className="gta-button text-lg md:text-xl font-street group relative overflow-hidden"
          onClick={() => {
            document.querySelector('#about').scrollIntoView({ 
              behavior: 'smooth' 
            });
          }}
        >
          <span className="relative z-10">JOIN THE CREW</span>
          <div className="absolute inset-0 bg-gradient-to-r from-neon-green to-neon-pink opacity-0 group-hover:opacity-20 transition-opacity duration-300"></div>
        </button>
      </div>

      {/* Animated cars */}
      <div className="absolute bottom-20 left-0 w-full overflow-hidden">
        <div className="car-animation flex space-x-96">
          <div className="w-16 h-8 bg-gradient-to-r from-gta-red to-red-600 rounded animate-pulse"></div>
          <div className="w-20 h-8 bg-gradient-to-r from-gta-blue to-blue-600 rounded animate-pulse"></div>
          <div className="w-14 h-8 bg-gradient-to-r from-gta-yellow to-yellow-600 rounded animate-pulse"></div>
        </div>
      </div>

      {/* Scroll indicator */}
      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce">
        <div className="w-6 h-10 border-2 border-neon-green rounded-full flex justify-center">
          <div className="w-1 h-3 bg-neon-green rounded-full mt-2 animate-pulse"></div>
        </div>
      </div>

      {/* Vignette effect */}
      <div className="absolute inset-0 bg-gradient-radial from-transparent via-transparent to-black opacity-60 pointer-events-none"></div>
    </section>
  );
};

export default Hero;
