import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

const About = () => {
  const sectionRef = useRef(null);
  const titleRef = useRef(null);
  const cardsRef = useRef(null);

  // Mock crew data with GTA-style characters
  const crewMembers = [
    {
      id: 1,
      name: "ALEX 'TURBO' RODRIGUEZ",
      role: "President",
      specialty: "Engine Tuning",
      avatar: "ðŸ‘¨â€ðŸ”§",
      stats: { respect: 95, skill: 88, experience: 92 },
      bio: "Master of high-performance engines and street racing legend. Joined SAE to channel his passion for speed into engineering excellence."
    },
    {
      id: 2,
      name: "MAYA 'CIRCUIT' CHEN",
      role: "Vice President",
      specialty: "Electronics & AI",
      avatar: "ðŸ‘©â€ðŸ’»",
      stats: { respect: 90, skill: 96, experience: 85 },
      bio: "Genius in automotive electronics and autonomous driving systems. Known for building the fastest ECU modifications in the city."
    },
    {
      id: 3,
      name: "JAKE 'DRIFT' MARTINEZ",
      role: "Events Coordinator",
      specialty: "Suspension & Handling",
      avatar: "ðŸŽï¸",
      stats: { respect: 85, skill: 91, experience: 89 },
      bio: "Professional drift racer turned engineer. Specializes in chassis dynamics and suspension geometry for ultimate handling."
    },
    {
      id: 4,
      name: "SARAH 'NITRO' WILLIAMS",
      role: "Technical Lead",
      specialty: "Fuel Systems",
      avatar: "âš¡",
      stats: { respect: 88, skill: 94, experience: 87 },
      bio: "Expert in fuel injection and forced induction systems. Her nitrous setups are legendary in the underground racing scene."
    },
    {
      id: 5,
      name: "CARLOS 'METAL' GARCIA",
      role: "Workshop Manager",
      specialty: "Fabrication",
      avatar: "ðŸ”¨",
      stats: { respect: 92, skill: 89, experience: 95 },
      bio: "Master fabricator with 20+ years experience. Can build anything from roll cages to custom exhaust systems with his eyes closed."
    },
    {
      id: 6,
      name: "ZARA 'DATA' JOHNSON",
      role: "Research Director",
      specialty: "Aerodynamics",
      avatar: "ðŸ“Š",
      stats: { respect: 87, skill: 93, experience: 83 },
      bio: "PhD in Aerodynamics who brings cutting-edge research to street-level applications. Her wind tunnel data saves seconds on the track."
    }
  ];

  useEffect(() => {
    const section = sectionRef.current;
    const title = titleRef.current;
    const cards = cardsRef.current;

    // Title animation
    gsap.fromTo(title, 
      { opacity: 0, y: 50 },
      {
        opacity: 1,
        y: 0,
        duration: 1,
        ease: "power3.out",
        scrollTrigger: {
          trigger: section,
          start: "top 80%",
          end: "top 50%",
          toggleActions: "play none none reverse"
        }
      }
    );

    // Cards stagger animation
    gsap.fromTo(cards.children,
      { opacity: 0, y: 100, rotateX: -15 },
      {
        opacity: 1,
        y: 0,
        rotateX: 0,
        duration: 0.8,
        ease: "back.out(1.7)",
        stagger: {
          each: 0.2,
          from: "start"
        },
        scrollTrigger: {
          trigger: cards,
          start: "top 85%",
          end: "bottom 60%",
          toggleActions: "play none none reverse"
        }
      }
    );

  }, []);

  const StatBar = ({ label, value, color }) => (
    <div className="mb-2">
      <div className="flex justify-between text-sm font-street text-gray-300 mb-1">
        <span>{label}</span>
        <span>{value}%</span>
      </div>
      <div className="w-full bg-gray-700 rounded-full h-2">
        <div 
          className={`h-2 rounded-full ${color} transition-all duration-1000 ease-out`}
          style={{ width: `${value}%` }}
        ></div>
      </div>
    </div>
  );

  const CrewCard = ({ member, index }) => {
    const cardRef = useRef(null);
    const [flipped, setFlipped] = React.useState(false);

    const handleCardHover = (isHover) => {
      const card = cardRef.current;
      if (isHover) {
        gsap.to(card, {
          scale: 1.05,
          rotateY: flipped ? 180 : 0,
          duration: 0.3,
          ease: "power2.out"
        });
      } else {
        gsap.to(card, {
          scale: 1,
          rotateY: flipped ? 180 : 0,
          duration: 0.3,
          ease: "power2.out"
        });
      }
    };

    const flipCard = () => {
      setFlipped(!flipped);
      gsap.to(cardRef.current, {
        rotateY: !flipped ? 180 : 0,
        duration: 0.6,
        ease: "power2.inOut"
      });
    };

    return (
      <div 
        ref={cardRef}
        className="character-card rounded-lg p-6 cursor-pointer transform-gpu perspective-1000"
        onMouseEnter={() => handleCardHover(true)}
        onMouseLeave={() => handleCardHover(false)}
        onClick={flipCard}
        style={{ transformStyle: 'preserve-3d' }}
      >
        {/* Front of card */}
        <div className={`${flipped ? 'opacity-0' : 'opacity-100'} transition-opacity duration-300`}>
          <div className="text-center mb-4">
