import React, { useEffect, useRef, useState } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

const Hero = () => {
  const heroRef = useRef(null);
  const titleRef = useRef(null);
  const subtitleRef = useRef(null);
  const buttonRef = useRef(null);
  const characterLayer1Ref = useRef(null);
  const characterLayer2Ref = useRef(null);
  const vehicleLayerRef = useRef(null);
  const cityLayerRef = useRef(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isLoading, setIsLoading] = useState(true);

  // GTA 5 character silhouettes (realistic proportions)
  const gtaCharacters = [
    {
      id: 1,
      name: "FRANKLIN",
      position: { x: 15, y: 20, scale: 1.2 },
      silhouette: `<svg viewBox="0 0 200 400" xmlns="http://www.w3.org/2000/svg">
        <path d="M100 30 C90 35, 85 45, 90 60 L95 80 C92 90, 88 100, 85 120 L80 140 C78 160, 75 180, 80 200 L85 240 C88 260, 92 280, 95 300 L98 340 C100 360, 102 380, 100 400 L95 390 C85 385, 75 380, 70 370 L65 350 C60 330, 55 310, 60 290 L65 270 C68 250, 70 230, 75 210 L80 190 C82 170, 85 150, 88 130 L92 110 C95 90, 98 70, 95 50 C100 40, 105 35, 100 30 Z" fill="url(#franklinGrad)"/>
        <defs>
          <linearGradient id="franklinGrad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#39ff14;stop-opacity:0.8" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
        </defs>
      </svg>`,
      color: "#39ff14"
    },
    {
      id: 2,
      name: "MICHAEL",
      position: { x: 75, y: 10, scale: 1.1 },
      silhouette: `<svg viewBox="0 0 200 400" xmlns="http://www.w3.org/2000/svg">
        <path d="M100 25 C95 30, 90 40, 95 55 L100 75 C98 85, 95 95, 92 115 L88 135 C85 155, 82 175, 87 195 L92 235 C95 255, 98 275, 102 295 L105 335 C108 355, 110 375, 108 395 L105 385 C95 380, 85 375, 80 365 L75 345 C70 325, 65 305, 70 285 L75 265 C78 245, 80 225, 85 205 L90 185 C92 165, 95 145, 98 125 L102 105 C105 85, 108 65, 105 45 C110 35, 115 30, 100 25 Z" fill="url(#michaelGrad)"/>
        <defs>
          <linearGradient id="michaelGrad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#f9c74f;stop-opacity:0.8" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
        </defs>
      </svg>`,
      color: "#f9c74f"
    },
    {
      id: 3,
      name: "TREVOR",
      position: { x: 85, y: 30, scale: 1.0 },
      silhouette: `<svg viewBox="0 0 200 400" xmlns="http://www.w3.org/2000/svg">
        <path d="M100 20 C92 25, 88 35, 93 50 L98 70 C95 80, 90 90, 87 110 L83 130 C80 150, 77 170, 82 190 L87 230 C90 250, 93 270, 97 290 L100 330 C103 350, 105 370, 103 390 L100 380 C90 375, 80 370, 75 360 L70 340 C65 320, 60 300, 65 280 L70 260 C73 240, 75 220, 80 200 L85 180 C87 160, 90 140, 93 120 L97 100 C100 80, 103 60, 100 40 C105 30, 110 25, 100 20 Z" fill="url(#trevorGrad)"/>
        <defs>
          <linearGradient id="trevorGrad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#e63946;stop-opacity:0.8" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
        </defs>
      </svg>`,
      color: "#e63946"
    }
  ];

  // GTA 5 vehicle silhouettes
  const gtaVehicles = [
    {
      id: 1,
      name: "INFERNUS",
      position: { x: 20, y: 60, scale: 0.8 },
      silhouette: `<svg viewBox="0 0 400 150" xmlns="http://www.w3.org/2000/svg">
        <path d="M40 90 L60 70 L100 60 L300 60 L340 70 L360 90 L370 110 L350 120 L330 115 L310 110 L90 110 L70 115 L50 120 L30 110 Z" fill="url(#infernusGrad)"/>
        <circle cx="100" cy="115" r="25" fill="url(#wheelGrad)"/>
        <circle cx="300" cy="115" r="25" fill="url(#wheelGrad)"/>
        <path d="M100 65 L120 55 L280 55 L300 65 L290 75 L110 75 Z" fill="url(#windowGrad)"/>
        <defs>
          <linearGradient id="infernusGrad" x1="0%" y1="0%" x2="100%" y2="0%">
            <stop offset="0%" style="stop-color:#e63946;stop-opacity:0.9" />
            <stop offset="50%" style="stop-color:#f9c74f;stop-opacity:0.7" />
            <stop offset="100%" style="stop-color:#e63946;stop-opacity:0.9" />
          </linearGradient>
          <linearGradient id="wheelGrad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#333;stop-opacity:1" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
          <linearGradient id="windowGrad" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#39ff14;stop-opacity:0.3" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:0.8" />
          </linearGradient>
        </defs>
      </svg>`,
      color: "#e63946"
    },
    {
      id: 2,
      name: "BANSHEE",
      position: { x: 70, y: 75, scale: 0.6 },
      silhouette: `<svg viewBox="0 0 400 150" xmlns="http://www.w3.org/2000/svg">
        <path d="M50 95 L70 75 L110 65 L290 65 L330 75 L350 95 L360 115 L340 125 L320 120 L300 115 L100 115 L80 120 L60 125 L40 115 Z" fill="url(#bansheeGrad)"/>
        <circle cx="110" cy="120" r="22" fill="url(#wheelGrad2)"/>
        <circle cx="290" cy="120" r="22" fill="url(#wheelGrad2)"/>
        <defs>
          <linearGradient id="bansheeGrad" x1="0%" y1="0%" x2="100%" y2="0%">
            <stop offset="0%" style="stop-color:#39ff14;stop-opacity:0.8" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
          <linearGradient id="wheelGrad2" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#444;stop-opacity:1" />
            <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
          </linearGradient>
        </defs>
      </svg>`,
      color: "#39ff14"
    }
  ];

  useEffect(() => {
    const hero = heroRef.current;
    const title = titleRef.current;
    const subtitle = subtitleRef.current;
    const button = buttonRef.current;
    const charLayer1 = characterLayer1Ref.current;
    const charLayer2 = characterLayer2Ref.current;
    const vehicleLayer = vehicleLayerRef.current;
    const cityLayer = cityLayerRef.current;

    // Loading sequence
    const loadingTl = gsap.timeline({
      onComplete: () => setIsLoading(false)
    });

    loadingTl
      .to(".loading-screen", { duration: 0.5, opacity: 1 })
      .to(".loading-bar", { duration: 2, width: "100%", ease: "power2.inOut" })
      .to(".loading-screen", { duration: 0.5, opacity: 0 });

    // Initial setup - everything hidden
    gsap.set([title, subtitle, button], { opacity: 0, y: 100, rotationX: -90 });
    gsap.set([charLayer1, charLayer2, vehicleLayer], { opacity: 0, scale: 0.5 });
    gsap.set(cityLayer, { opacity: 0.3, scale: 1.2 });

    // Cinematic entrance timeline
    const masterTl = gsap.timeline({ delay: 3 });

    // City backdrop entrance
    masterTl.to(cityLayer, {
      duration: 2,
      opacity: 0.6,
      scale: 1,
      ease: "power2.out"
    });

    // Characters entrance with stagger
    masterTl.to([charLayer1, charLayer2], {
      duration: 1.5,
      opacity: 0.8,
      scale: 1,
      ease: "back.out(1.7)",
      stagger: 0.3
    }, "-=1");

    // Vehicles entrance
    masterTl.to(vehicleLayer, {
      duration: 1.2,
      opacity: 0.9,
      scale: 1,
      ease: "power3.out"
    }, "-=0.8");

    // Title dramatic entrance
    masterTl.to(title, {
      duration: 1.5,
      opacity: 1,
      y: 0,
      rotationX: 0,
      ease: "power4.out",
      onComplete: () => {
        // Start continuous glow animation
        gsap.to(title, {
          textShadow: "0 0 20px #39ff14, 0 0 40px #39ff14, 0 0 60px #39ff14",
          duration: 2,
          yoyo: true,
          repeat: -1,
          ease: "power2.inOut"
        });
      }
    }, "-=0.5");

    // Subtitle entrance
    masterTl.to(subtitle, {
      duration: 1,
      opacity: 1,
      y: 0,
      rotationX: 0,
      ease: "power3.out"
    }, "-=1");

    // Button entrance
    masterTl.to(button, {
      duration: 0.8,
      opacity: 1,
      y: 0,
      rotationX: 0,
      ease: "back.out(2)"
    }, "-=0.5");

    // Advanced parallax with multiple layers
    const parallaxTl = gsap.timeline({
      scrollTrigger: {
        trigger: hero,
        start: "top top",
        end: "bottom top",
        scrub: 1.5,
      }
    });

    parallaxTl
      .to(cityLayer, { y: -150, scale: 1.3, ease: "none" }, 0)
      .to(charLayer1, { y: -100, x: 50, rotationY: 5, ease: "none" }, 0)
      .to(charLayer2, { y: -80, x: -30, rotationY: -3, ease: "none" }, 0)
      .to(vehicleLayer, { y: -60, ease: "none" }, 0)
      .to([title, subtitle], { y: -200, ease: "none" }, 0);

    // Mouse interaction effects
    const handleMouseMove = (e) => {
      const { clientX, clientY } = e;
      const { innerWidth, innerHeight } = window;

      const xPercent = (clientX / innerWidth - 0.5) * 2;
      const yPercent = (clientY / innerHeight - 0.5) * 2;

      setMousePosition({ x: xPercent, y: yPercent });

      // Subtle character following
      gsap.to(charLayer1, {
        x: xPercent * 20,
        y: yPercent * 10,
        rotationY: xPercent * 5,
        duration: 1,
        ease: "power2.out"
      });

      gsap.to(charLayer2, {
        x: xPercent * -15,
        y: yPercent * 8,
        rotationY: xPercent * -3,
        duration: 1.2,
        ease: "power2.out"
      });
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Continuous ambient animations
    gsap.to(".street-light-glow", {
      opacity: 0.3,
      duration: 1.5,
      yoyo: true,
      repeat: -1,
      stagger: {
        each: 0.4,
        from: "random"
      }
    });

    // Vehicle movement
    gsap.to(".vehicle-move", {
      x: "100vw",
      duration: 8,
      ease: "none",
      repeat: -1,
      delay: () => Math.random() * 5
    });

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      masterTl.kill();
      parallaxTl.kill();
    };
  }, []);

  const scrollToNext = () => {
    document.querySelector('#about').scrollIntoView({ 
      behavior: 'smooth' 
    });
  };

  return (
    <>
      {/* Loading Screen */}
      {isLoading && (
        <div className="loading-screen fixed inset-0 bg-black z-50 flex items-center justify-center">
          <div className="text-center">
            <div className="gta-title text-4xl neon-text mb-8">LOADING SAE EMPIRE</div>
            <div className="w-64 h-2 bg-gray-800 rounded-full overflow-hidden">
              <div className="loading-bar h-full w-0 bg-gradient-to-r from-neon-green via-gta-yellow to-gta-red"></div>
            </div>
            <div className="mt-4 font-street text-gray-400">Preparing your automotive experience...</div>
          </div>
        </div>
      )}

      <section 
        ref={heroRef}
        className="relative h-screen overflow-hidden bg-black magnetic-cursor"
        style={{ perspective: '1000px' }}
      >
        {/* Multi-layer Background */}

        {/* City Layer (Deepest) */}
        <div 
          ref={cityLayerRef}
          className="parallax-layer-1 absolute inset-0 opacity-30"
          style={{
            backgroundImage: `url("data:image/svg+xml,${encodeURIComponent(`
              <svg viewBox="0 0 1920 1080" xmlns="http://www.w3.org/2000/svg">
                <defs>
                  <linearGradient id="cityGlow" x1="0%" y1="100%" x2="0%" y2="0%">
                    <stop offset="0%" style="stop-color:#000;stop-opacity:1" />
                    <stop offset="30%" style="stop-color:#1a1a1d;stop-opacity:0.9" />
                    <stop offset="60%" style="stop-color:#2a2a2d;stop-opacity:0.7" />
                    <stop offset="100%" style="stop-color:#39ff14;stop-opacity:0.2" />
                  </linearGradient>
                  <linearGradient id="buildingGrad" x1="0%" y1="0%" x2="0%" y2="100%">
                    <stop offset="0%" style="stop-color:#333;stop-opacity:0.8" />
                    <stop offset="100%" style="stop-color:#000;stop-opacity:1" />
                  </linearGradient>
                </defs>
                <rect width="1920" height="1080" fill="url(#cityGlow)"/>

                <!-- Skyscrapers with windows -->
                <rect x="100" y="400" width="120" height="680" fill="url(#buildingGrad)"/>
                <rect x="110" y="450" width="8" height="12" fill="#f9c74f" opacity="0.8"/>
                <rect x="130" y="480" width="8" height="12" fill="#39ff14" opacity="0.6"/>
                <rect x="150" y="520" width="8" height="12" fill="#e63946" opacity="0.7"/>
                <rect x="170" y="460" width="8" height="12" fill="#f9c74f" opacity="0.5"/>
                <rect x="190" y="500" width="8" height="12" fill="#39ff14" opacity="0.8"/>

                <rect x="300" y="300" width="150" height="780" fill="url(#buildingGrad)"/>
                <rect x="320" y="350" width="10" height="15" fill="#39ff14" opacity="0.8"/>
                <rect x="350" y="380" width="10" height="15" fill="#f9c74f" opacity="0.6"/>
                <rect x="380" y="420" width="10" height="15" fill="#e63946" opacity="0.7"/>
                <rect x="410" y="360" width="10" height="15" fill="#39ff14" opacity="0.9"/>

                <rect x="500" y="350" width="130" height="730" fill="url(#buildingGrad)"/>
                <rect x="520" y="400" width="8" height="12" fill="#f9c74f" opacity="0.7"/>
                <rect x="540" y="440" width="8" height="12" fill="#39ff14" opacity="0.8"/>
                <rect x="560" y="480" width="8" height="12" fill="#e63946" opacity="0.6"/>

                <rect x="700" y="250" width="180" height="830" fill="url(#buildingGrad)"/>
                <rect x="730" y="300" width="12" height="18" fill="#39ff14" opacity="0.9"/>
                <rect x="760" y="340" width="12" height="18" fill="#f9c74f" opacity="0.7"/>
                <rect x="790" y="380" width="12" height="18" fill="#e63946" opacity="0.8"/>
                <rect x="820" y="320" width="12" height="18" fill="#39ff14" opacity="0.6"/>

                <rect x="950" y="320" width="140" height="760" fill="url(#buildingGrad)"/>
                <rect x="1150" y="200" width="200" height="880" fill="url(#buildingGrad)"/>
                <rect x="1400" y="380" width="160" height="700" fill="url(#buildingGrad)"/>
                <rect x="1620" y="450" width="120" height="630" fill="url(#buildingGrad)"/>

                <!-- Street level with lights -->
                <rect x="0" y="900" width="1920" height="180" fill="#1a1a1d"/>
                <rect x="0" y="880" width="1920" height="20" fill="#333"/>

                <!-- Street lights -->
                <circle cx="200" cy="870" r="8" fill="#f9c74f" opacity="0.8" class="street-light-glow"/>
                <circle cx="500" cy="870" r="8" fill="#f9c74f" opacity="0.8" class="street-light-glow"/>
                <circle cx="800" cy="870" r="8" fill="#f9c74f" opacity="0.8" class="street-light-glow"/>
                <circle cx="1200" cy="870" r="8" fill="#f9c74f" opacity="0.8" class="street-light-glow"/>
                <circle cx="1600" cy="870" r="8" fill="#f9c74f" opacity="0.8" class="street-light-glow"/>
              </svg>
            `)}")`,
            backgroundSize: 'cover',
            backgroundPosition: 'center bottom',
            backgroundRepeat: 'no-repeat'
          }}
        />

        {/* Character Layer 1 (Michael & Trevor) */}
        <div ref={characterLayer1Ref} className="parallax-layer-2 absolute inset-0">
          {gtaCharacters.slice(1).map((character) => (
            <div
              key={character.id}
              className="absolute opacity-80"
              style={{
                left: `${character.position.x}%`,
                top: `${character.position.y}%`,
                transform: `scale(${character.position.scale})`,
                filter: `drop-shadow(0 0 20px ${character.color})`
              }}
              dangerouslySetInnerHTML={{ __html: character.silhouette }}
            />
          ))}
        </div>

        {/* Character Layer 2 (Franklin) - Foreground */}
        <div ref={characterLayer2Ref} className="parallax-layer-3 absolute inset-0">
          <div
            className="absolute opacity-90"
            style={{
              left: `${gtaCharacters[0].position.x}%`,
              top: `${gtaCharacters[0].position.y}%`,
              transform: `scale(${gtaCharacters[0].position.scale})`,
              filter: `drop-shadow(0 0 30px ${gtaCharacters[0].color})`
            }}
            dangerouslySetInnerHTML={{ __html: gtaCharacters[0].silhouette }}
          />
        </div>

        {/* Vehicle Layer */}
        <div ref={vehicleLayerRef} className="parallax-layer-4 absolute inset-0">
          {gtaVehicles.map((vehicle) => (
            <div
              key={vehicle.id}
              className="absolute opacity-75 vehicle-move"
              style={{
                left: `${vehicle.position.x}%`,
                top: `${vehicle.position.y}%`,
                transform: `scale(${vehicle.position.scale})`,
                filter: `drop-shadow(0 0 15px ${vehicle.color})`
              }}
              dangerouslySetInnerHTML={{ __html: vehicle.silhouette }}
            />
          ))}
        </div>

        {/* HUD Elements */}
        <div className="absolute top-8 left-8 z-20">
          <div className="flex items-center space-x-4 font-street text-sm">
            <div className="money-counter">$2,547,891</div>
            <div className="flex space-x-1">
              <span className="wanted-star">★</span>
              <span className="wanted-star">★</span>
              <span className="wanted-star">★</span>
              <span className="text-gray-600">★</span>
              <span className="text-gray-600">★</span>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="parallax-layer-4 relative z-10 flex flex-col items-center justify-center h-full text-center px-4">

          {/* Main Title */}
          <h1 
            ref={titleRef}
            className="gta-title text-8xl md:text-9xl lg:text-[12rem] mb-8 neon-text"
            style={{
              textShadow: `
                0 0 10px #39ff14,
                0 0 20px #39ff14,
                0 0 30px #39ff14,
                0 0 40px #39ff14,
                0 2px 0 rgba(0,0,0,0.9),
                0 4px 0 rgba(0,0,0,0.7),
                0 6px 0 rgba(0,0,0,0.5),
                0 8px 0 rgba(0,0,0,0.3),
                0 10px 20px rgba(0,0,0,0.8)
