          <div className="w-64 h-48 relative overflow-hidden"
               style={{
                 borderRadius: '8px',
                 border: '2px solid rgba(255, 255, 255, 0.3)',
                 boxShadow: `
                   0 0 20px rgba(0, 0, 0, 0.6),
                   inset 0 0 8px rgba(0, 0, 0, 0.3)
                 `,
                 backgroundColor: 'rgba(26, 35, 50, 0.4)',
                 backdropFilter: 'blur(4px)'
               }}>

            {/* ENHANCED DETAILED SATELLITE MAP WITH VISIBLE ROADS AND BUILDINGS */}
            <div className="absolute inset-0 w-full h-full opacity-80">
              <img 
                src="data:image/svg+xml;base64,"
                alt="Enhanced Los Santos Satellite Map"
                className="w-full h-full object-cover"
                style={{
                  filter: 'contrast(1.2) brightness(0.85) saturate(1.3) hue-rotate(8deg)'
                }}
              />
            </div>

            {/* MAP OVERLAY UI ELEMENTS */}
            <div className="absolute inset-0 pointer-events-none">
              {/* Compass */}
              <div className="absolute top-2 right-2 w-8 h-8 bg-black/70 rounded-full flex items-center justify-center border border-white/30">
                <span className="text-white text-xs font-bold">N</span>
                <div className="absolute top-1 w-px h-2 bg-red-500"></div>
              </div>

              {/* Zoom level indicator */}
              <div className="absolute bottom-2 right-2 text-white text-xs bg-black/70 px-2 py-1 rounded">
                1:2500
              </div>

              {/* Grid lines for authenticity */}
              <div className="absolute inset-0 opacity-20" style={{
                backgroundImage: `
                  linear-gradient(to right, rgba(255,255,255,0.1) 1px, transparent 1px),
                  linear-gradient(to bottom, rgba(255,255,255,0.1) 1px, transparent 1px)
                `,
                backgroundSize: '32px 24px'
              }}></div>
            </div>

            {/* PLAYER POSITION - WHITE TRIANGLE */}
            <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 pointer-events-none z-30">
              <div style={{
                width: '0',
                height: '0',
                borderLeft: '8px solid transparent',
                borderRight: '8px solid transparent', 
                borderBottom: '16px solid #ffffff',
                filter: 'drop-shadow(0 0 8px rgba(255, 255, 255, 1)) drop-shadow(0 2px 6px rgba(0, 0, 0, 0.9))',
                transform: 'rotate(0deg)'
              }}></div>
            </div>

            {/* SAE HQ WAYPOINT - Downtown Area */}
            <div 
              ref={pointerRef}
              className="absolute cursor-pointer z-20"
              style={{ top: '46%', left: '50%', transform: 'translate(-50%, -50%)' }}
              onMouseEnter={() => {
                setShowTooltip(true);
                if (window.handleTooltipShow) window.handleTooltipShow();
              }}
              onMouseLeave={() => {
                setShowTooltip(false);
                if (window.handleTooltipHide) window.handleTooltipHide();
              }}
            >
              <div className="w-4 h-4 bg-gradient-to-br from-yellow-300 to-yellow-500 rounded-full relative border-2 border-white"
                   style={{
                     boxShadow: '0 0 12px rgba(251, 191, 36, 0.9), inset 0 1px 2px rgba(255, 255, 255, 0.5)'
                   }}>
                <div className="absolute inset-0 flex items-center justify-center text-xs font-black text-black">S</div>
                <div className="absolute -inset-1 bg-yellow-400/20 rounded-full animate-pulse"></div>
              </div>
            </div>

            {/* GARAGE - Industrial Zone */}
            <div className="absolute top-[50%] left-[62%] w-3 h-3 bg-orange-500 rounded-full border border-white"
                 style={{ boxShadow: '0 0 6px rgba(249, 115, 22, 0.8)' }}></div>

            {/* RACE START - Vinewood */}
            <div className="absolute top-[30%] left-[75%] w-3 h-3 bg-red-500 rounded-full border border-white"
                 style={{ boxShadow: '0 0 6px rgba(239, 68, 68, 0.8)' }}></div>

            {/* PARTS SHOP - Beach */}
            <div className="absolute top-[80%] left-[35%] w-2.5 h-2.5 bg-blue-500 rounded-full border border-white"
                 style={{ boxShadow: '0 0 5px rgba(59, 130, 246, 0.8)' }}></div>

            {/* MEETUP - Airport */}
            <div className="absolute top-[82%] left-[28%] w-2.5 h-2.5 bg-purple-500 rounded-full border border-white"
                 style={{ boxShadow: '0 0 5px rgba(168, 85, 247, 0.8)' }}></div>

            {/* ADDITIONAL BLIPS */}
            <div className="absolute top-[25%] left-[35%] w-2 h-2 bg-green-500 rounded-full border border-white/70"
                 style={{ boxShadow: '0 0 4px rgba(34, 197, 94, 0.8)' }}></div>

            <div className="absolute top-[65%] left-[78%] w-2 h-2 bg-pink-500 rounded-full border border-white/70"
                 style={{ boxShadow: '0 0 4px rgba(236, 72, 153, 0.8)' }}></div>

          </div>

          {/* GTA 5 STYLE HUD ELEMENTS */}
          <div className="absolute -bottom-6 left-0 right-0 flex items-center justify-between">
            {/* Health/Armor bars */}
            <div className="flex items-center space-x-2">
              <div className="w-14 h-1 bg-black/80 rounded-full border border-white/30 overflow-hidden">
                <div className="h-full bg-green-500 rounded-full" style={{ width: '87%' }}></div>
              </div>
              <div className="w-10 h-1 bg-black/80 rounded-full border border-white/30 overflow-hidden">
                <div className="h-full bg-blue-400 rounded-full" style={{ width: '62%' }}></div>
              </div>
            </div>

            {/* Mini HUD icons */}
            <div className="flex items-center space-x-1">
              <div className="w-2.5 h-2.5 bg-white/90 rounded text-black text-xs flex items-center justify-center font-bold">
                📞
              </div>
              <div className="w-2.5 h-2.5 bg-white/90 rounded text-black text-xs flex items-center justify-center font-bold">
                🎵
              </div>
            </div>
          </div>

          {/* ENHANCED TOOLTIP */}
          <div 
            ref={tooltipRef}
            className="absolute -top-16 left-1/2 transform -translate-x-1/2 bg-black/95 backdrop-blur-sm text-white px-4 py-3 rounded-lg border border-white/30 whitespace-nowrap text-sm font-medium opacity-0 scale-0"
            style={{ fontFamily: 'Arial, sans-serif' }}
          >
            <div className="flex items-center space-x-2">
              <span className="text-yellow-400">⚡</span>
              <span className="typewriter-text">SAE AUTO EMPIRE HQ</span>
            </div>
            <div className="absolute top-full left-1/2 transform -translate-x-1/2 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-white/30"></div>
          </div>
        </div>

        {/* Welcome Character Card - Top Right */}
        <div className="absolute top-6 right-6 z-30">
          <div className="bg-black/80 backdrop-blur-md rounded-lg p-4 border-2 border-black/60 w-80 transform hover:scale-105 transition-all duration-300 shadow-2xl">
            {/* Header */}
            <div className="flex items-center space-x-3 mb-3">
              <div className="w-12 h-12 bg-gradient-to-br from-gray-700 to-black rounded-full flex items-center justify-center border border-white/20">
                <span className="text-white font-black text-lg">👥</span>
              </div>
              <div>
                <h3 className="text-white font-bold text-sm tracking-wider"
                    style={{ fontFamily: 'Bebas Neue, Impact, Arial Black, sans-serif', letterSpacing: '0.1em' }}>
                  WELCOME EVERYONE!
                </h3>
                <p className="text-gray-300 text-xs font-medium"
                   style={{ fontFamily: 'Arial, sans-serif' }}>
                  Society of Automotive Engineers
                </p>
              </div>
            </div>

            {/* Welcome Message */}
            <div className="mb-4">
              <p className="text-white text-sm leading-relaxed font-medium"
                 style={{ fontFamily: 'Arial, sans-serif' }}>
                Welcome to our elite automotive empire! Your reputation precedes you in Los Santos.
              </p>
            </div>

            {/* Client Level */}
            <div className="border-t border-black/30 pt-3">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-400 text-xs uppercase tracking-wider font-bold"
                     style={{ fontFamily: 'Arial, sans-serif' }}>Your Level</p>
                  <p className="text-white font-bold text-lg"
                     style={{ fontFamily: 'Orbitron, Exo 2, Arial Black, sans-serif', letterSpacing: '0.05em' }}>STREET LEGEND</p>
                </div>
                <div className="text-right">
                  <div className="flex space-x-1 mb-1">
                    {[...Array(5)].map((_, i) => (
                      <span key={i} className={`text-lg ${
                        i < 4 ? 'text-yellow-400' : 'text-gray-600'
                      }`}>★</span>
                    ))}
                  </div>
                  <p className="text-yellow-400 text-xs font-bold"
                     style={{ fontFamily: 'Arial, sans-serif' }}>4/5 STARS</p>
                </div>
              </div>
            </div>

            {/* Pulsing Border Effect */}
            <div className="absolute inset-0 border-2 border-black/20 rounded-lg animate-pulse pointer-events-none"></div>
          </div>
        </div>

        {/* Bottom fade to black for smooth transition */}
        <div className="absolute bottom-0 left-0 w-full h-32 bg-gradient-to-t from-black to-transparent" />
      </section>
    </>
  );
};

export default Hero;
