                  <!-- Base terrain/ground -->
                  <rect width="300" height="300" fill="#2a3142"/>

                  <!-- Water bodies (ocean/river) -->
                  <path d="M0 0 L80 0 L75 40 Q70 50 65 60 L60 80 Q55 100 50 120 L45 150 Q40 180 35 220 L30 260 Q25 280 20 300 L0 300 Z" fill="url(#water)"/>
                  <path d="M300 200 Q280 195 260 200 L240 205 Q220 210 200 215 L180 220 Q160 225 140 230 L120 235 Q100 240 80 250 L70 260 Q75 280 80 300 L300 300 Z" fill="url(#water)"/>

                  <!-- Downtown/City Center (dense buildings) -->
                  <rect x="120" y="80" width="80" height="60" fill="url(#buildings)"/>
                  <rect x="140" y="60" width="40" height="20" fill="#1a1f2e"/>
                  <rect x="130" y="140" width="60" height="40" fill="url(#buildings)"/>

                  <!-- Residential areas -->
                  <rect x="80" y="40" width="60" height="40" fill="#2d3748" opacity="0.8"/>
                  <rect x="200" y="100" width="50" height="60" fill="#2d3748" opacity="0.8"/>
                  <rect x="60" y="160" width="50" height="50" fill="#2d3748" opacity="0.8"/>

                  <!-- Industrial area -->
                  <rect x="220" y="40" width="60" height="40" fill="#3a3a4a"/>
                  <rect x="230" y="35" width="15" height="5" fill="#4a4a5a"/>
                  <rect x="250" y="37" width="12" height="3" fill="#4a4a5a"/>

                  <!-- Parks/Green areas -->
                  <ellipse cx="90" cy="120" rx="20" ry="15" fill="url(#vegetation)"/>
                  <ellipse cx="250" cy="180" rx="25" ry="20" fill="url(#vegetation)"/>
                  <rect x="40" y="220" width="30" height="25" fill="url(#vegetation)" rx="5"/>

                  <!-- Major Highways (thick white roads) -->
                  <path d="M0 150 Q50 148 100 150 L150 150 Q200 152 250 150 L300 150" 
                        stroke="#e0e0e0" stroke-width="6" fill="none" opacity="0.9"/>
                  <path d="M150 0 Q148 50 150 100 L150 150 Q152 200 150 250 L150 300" 
                        stroke="#e0e0e0" stroke-width="6" fill="none" opacity="0.9"/>

                  <!-- Secondary highways -->
                  <path d="M50 80 Q100 85 150 80 L200 80 Q240 82 280 80" 
                        stroke="#c0c0c0" stroke-width="4" fill="none" opacity="0.8"/>
                  <path d="M80 0 Q82 40 80 80 L80 120 Q78 160 80 200 L80 240 Q82 270 80 300" 
                        stroke="#c0c0c0" stroke-width="4" fill="none" opacity="0.8"/>
                  <path d="M220 0 Q218 50 220 100 L220 150 Q222 200 220 250 L220 300" 
                        stroke="#c0c0c0" stroke-width="4" fill="none" opacity="0.8"/>

                  <!-- City streets (grid pattern) -->
                  <path d="M100 40 L100 280" stroke="#a0a0a0" stroke-width="2" fill="none" opacity="0.6"/>
                  <path d="M120 40 L120 280" stroke="#a0a0a0" stroke-width="2" fill="none" opacity="0.6"/>
                  <path d="M180 40 L180 280" stroke="#a0a0a0" stroke-width="2" fill="none" opacity="0.6"/>
                  <path d="M200 40 L200 280" stroke="#a0a0a0" stroke-width="2" fill="none" opacity="0.6"/>

                  <path d="M40 100 L280 100" stroke="#a0a0a0" stroke-width="2" fill="none" opacity="0.6"/>
                  <path d="M40 120 L280 120" stroke="#a0a0a0" stroke-width="2" fill="none" opacity="0.6"/>
                  <path d="M40 180 L280 180" stroke="#a0a0a0" stroke-width="2" fill="none" opacity="0.6"/>
                  <path d="M40 200 L280 200" stroke="#a0a0a0" stroke-width="2" fill="none" opacity="0.6"/>

                  <!-- Smaller residential streets -->
                  <path d="M60 60 L60 140" stroke="#808080" stroke-width="1.5" fill="none" opacity="0.5"/>
                  <path d="M240 120 L240 240" stroke="#808080" stroke-width="1.5" fill="none" opacity="0.5"/>
                  <path d="M160 60 L280 60" stroke="#808080" stroke-width="1.5" fill="none" opacity="0.5"/>
                  <path d="M80 220 L200 220" stroke="#808080" stroke-width="1.5" fill="none" opacity="0.5"/>

                  <!-- Highway ramps and overpasses -->
                  <path d="M140 140 Q145 145 150 150 Q155 155 160 160" 
