                  <path d="M70 140 Q75 145 80 150 Q85 155 90 160" 
                        stroke="#d0d0d0" stroke-width="3" fill="none" opacity="0.7"/>

                  <!-- Landmarks/Special buildings -->
                  <rect x="145" y="145" width="10" height="10" fill="#4a5a6a" rx="1"/>
                  <rect x="75" y="145" width="10" height="10" fill="#4a5a6a" rx="1"/>
                  <rect x="180" y="80" width="8" height="12" fill="#5a4a4a"/>
                  <rect x="95" y="100" width="12" height="8" fill="#5a4a4a"/>

                  <!-- Beaches/Sand areas -->
                  <path d="M0 30 Q20 35 40 30 L60 28 Q80 25 100 30 L0 50 Z" fill="#4a453a" opacity="0.6"/>
                  <path d="M200 280 Q220 275 240 280 L280 285 Q300 290 300 300 L200 300 Z" fill="#4a453a" opacity="0.6"/>
                </svg>
