import React, { useEffect, useRef, useState } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

// Import the authentic GTA V backgrounds
import background1 from '../assets/background (1).jpg'; // Neon diner at sunset
import background2 from '../assets/background (2).jpg'; // Trevor with palm trees
import background3 from '../assets/background (3).jpg'; // City skyline sunset
import background4 from '../assets/background (4).jpg'; // Street racing at sunset
import background5 from '../assets/background (5).jpg'; // Beach reflection sunset

const Hero = () => {
  const heroRef = useRef(null);
  const titleRef = useRef(null);
  const subtitleRef = useRef(null);
  const buttonRef = useRef(null);
  const backgroundRef = useRef(null);
  const overlayRef = useRef(null);
  const hudRef = useRef(null);
  const [currentBg, setCurrentBg] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  const backgrounds = [background1, background2, background3, background4, background5];

  const backgroundTexts = [
    "WELCOME TO LOS SANTOS AUTO EMPIRE",
    "WHERE LEGENDS ARE BUILT",
    "ENGINEERING EXCELLENCE MEETS STREET CULTURE", 
    "SPEED IS OUR RELIGION",
    "PARADISE FOR AUTOMOTIVE GODS"
  ];

  useEffect(() => {
    const hero = heroRef.current;
    const title = titleRef.current;
    const subtitle = subtitleRef.current;
    const button = buttonRef.current;
    const background = backgroundRef.current;
    const overlay = overlayRef.current;
    const hud = hudRef.current;

    // GTA V Loading sequence with Vice City vibes
    const loadingTl = gsap.timeline({
      onComplete: () => setIsLoading(false)
    });

    loadingTl
      .to(".loading-screen", { duration: 0.8, opacity: 1 })
      .to(".loading-text", { 
        duration: 2, 
        text: "LOADING SAE AUTO EMPIRE...", 
        ease: "none" 
      })
      .to(".loading-bar", { 
        duration: 3, 
        width: "100%", 
        ease: "power2.inOut" 
      })
      .to(".loading-screen", { 
        duration: 1, 
        opacity: 0,
        delay: 0.5
      });

    // Background cycling animation
    const bgCycle = gsap.timeline({ repeat: -1 });
    backgrounds.forEach((_, index) => {
      bgCycle.call(() => setCurrentBg(index), null, index * 8);
    });

    // Initial setup - everything hidden with GTA V style
    gsap.set([title, subtitle, button], { 
      opacity: 0, 
      y: 150, 
      rotationX: -45,
      filter: "blur(20px)"
    });
    gsap.set(hud, { opacity: 0, x: -100 });
    gsap.set(overlay, { opacity: 0 });

    // Epic cinematic entrance timeline
    const masterTl = gsap.timeline({ delay: 4.5 });

    // Background fade in with atmospheric effect
    masterTl.to(background, {
      duration: 2,
      opacity: 1,
      scale: 1.05,
      ease: "power2.out"
    });

    // Overlay with Vice City atmosphere
    masterTl.to(overlay, {
      duration: 1.5,
      opacity: 0.3,
      ease: "power2.out"
    }, "-=1.5");

    // HUD entrance like GTA V
    masterTl.to(hud, {
      duration: 1,
      opacity: 1,
      x: 0,
      ease: "back.out(1.7)"
    }, "-=1");

    // Epic title entrance with perspective
    masterTl.to(title, {
      duration: 2,
      opacity: 1,
      y: 0,
      rotationX: 0,
      filter: "blur(0px)",
      ease: "power4.out",
      onComplete: () => {
        // Continuous neon glow pulsing
        gsap.to(title, {
          textShadow: `
            0 0 20px #ff0080,
            0 0 40px #ff0080,
            0 0 60px #ff0080,
            0 0 80px #ff0080,
            0 0 100px #ff0080
          `,
          duration: 2,
          yoyo: true,
          repeat: -1,
          ease: "power2.inOut"
        });
      }
    }, "-=0.8");

    // Subtitle with typing effect
    masterTl.to(subtitle, {
      duration: 1.5,
      opacity: 1,
      y: 0,
      rotationX: 0,
      filter: "blur(0px)",
      ease: "power3.out"
    }, "-=1.2");

    // Button with Vice City glow
    masterTl.to(button, {
      duration: 1,
      opacity: 1,
      y: 0,
      rotationX: 0,
      filter: "blur(0px)",
      ease: "back.out(2)"
    }, "-=0.8");

    // Advanced parallax with multiple atmospheric layers
    const parallaxTl = gsap.timeline({
      scrollTrigger: {
        trigger: hero,
        start: "top top",
        end: "bottom top",
        scrub: 1.5,
      }
    });

    parallaxTl
      .to(background, { 
        y: -200, 
        scale: 1.2, 
        filter: "brightness(0.7)",
        ease: "none" 
      }, 0)
      .to(overlay, { 
        opacity: 0.6, 
        ease: "none" 
      }, 0)
      .to([title, subtitle], { 
        y: -300, 
        opacity: 0.8,
        ease: "none" 
      }, 0);

    // Dynamic background switching with smooth transitions
    const handleBgSwitch = () => {
      gsap.to(background, {
        opacity: 0,
        duration: 1,
        onComplete: () => {
          setCurrentBg((prev) => (prev + 1) % backgrounds.length);
          gsap.to(background, {
            opacity: 1,
            duration: 1
          });
        }
      });
    };

    // Auto background switching every 8 seconds
    const bgInterval = setInterval(handleBgSwitch, 8000);

    // Mouse parallax for immersive experience
    const handleMouseMove = (e) => {
      const { clientX, clientY } = e;
      const { innerWidth, innerHeight } = window;

      const xPercent = (clientX / innerWidth - 0.5) * 2;
      const yPercent = (clientY / innerHeight - 0.5) * 2;

      gsap.to(background, {
        x: xPercent * 30,
        y: yPercent * 20,
        duration: 2,
        ease: "power2.out"
      });

      gsap.to(overlay, {
        x: xPercent * -15,
        y: yPercent * -10,
        duration: 2.5,
        ease: "power2.out"
      });
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Ambient Vice City effects
    gsap.to(".neon-flicker", {
      opacity: 0.5,
      duration: 0.1,
      yoyo: true,
      repeat: -1,
      stagger: {
        each: 0.05,
        from: "random"
      }
    });

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      clearInterval(bgInterval);
      masterTl.kill();
      parallaxTl.kill();
    };
  }, [backgrounds.length]);

  const scrollToNext = () => {
    document.querySelector('#about').scrollIntoView({ 
      behavior: 'smooth' 
    });
  };

  return (
    <>
      {/* GTA V Style Loading Screen */}
      {isLoading && (
        <div className="loading-screen fixed inset-0 bg-black z-50 flex items-center justify-center">
          <div className="text-center">
            <div className="gta-title text-6xl mb-8" style={{
              color: '#ff0080',
              textShadow: `
                0 0 10px #ff0080,
                0 0 20px #ff0080,
                0 0 30px #ff0080,
                0 0 40px #ff0080
              `
            }}>
              SAE
            </div>
            <div className="loading-text font-street text-xl text-white mb-8">
              LOADING SAE AUTO EMPIRE...
            </div>
            <div className="w-96 h-3 bg-gray-800 rounded-full overflow-hidden border border-gray-600">
              <div className="loading-bar h-full w-0 bg-gradient-to-r from-pink-500 via-purple-500 to-cyan-500"></div>
            </div>
            <div className="mt-6 font-street text-gray-400 text-sm">
              Entering Los Santos' Most Elite Automotive Empire...
            </div>
          </div>
        </div>
      )}

      <section 
        ref={heroRef}
        className="relative h-screen overflow-hidden"
        style={{ perspective: '2000px' }}
      >
        {/* Dynamic GTA V Background */}
        <div 
          ref={backgroundRef}
          className="absolute inset-0 opacity-0 transition-all duration-1000"
          style={{
            backgroundImage: `url(${backgrounds[currentBg]})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat',
            filter: 'brightness(0.9) contrast(1.1) saturate(1.2)'
          }}
        />

        {/* Vice City Atmospheric Overlay */}
        <div 
          ref={overlayRef}
          className="absolute inset-0 opacity-0"
          style={{
            background: `
              radial-gradient(circle at 70% 30%, rgba(255, 0, 128, 0.3) 0%, transparent 50%),
              radial-gradient(circle at 30% 70%, rgba(0, 255, 255, 0.2) 0%, transparent 50%),
              linear-gradient(45deg, rgba(255, 100, 0, 0.1) 0%, rgba(255, 0, 128, 0.1) 50%, rgba(0, 255, 255, 0.1) 100%)
            `
          }}
        />

        {/* GTA V Style HUD */}
        <div ref={hudRef} className="absolute top-8 left-8 z-20">
          <div className="bg-black bg-opacity-60 rounded-lg p-4 border border-pink-500">
            <div className="flex items-center space-x-6 font-street text-sm">
              <div className="flex items-center space-x-2">
                <div className="w-8 h-8 rounded-full bg-gradient-to-r from-pink-500 to-purple-500 flex items-center justify-center">
                  <span className="text-white font-bold text-xs">SAE</span>
                </div>
                <div>
                  <div className="text-pink-400 font-bold">LOS SANTOS</div>
                  <div className="text-gray-400 text-xs">AUTO EMPIRE</div>
                </div>
              </div>

              <div className="border-l border-gray-600 pl-4">
                <div className="money-counter text-lg">$2,547,891</div>
                <div className="text-gray-400 text-xs">TOTAL EARNINGS</div>
              </div>

              <div className="border-l border-gray-600 pl-4">
                <div className="flex space-x-1">
                  <span className="wanted-star text-pink-500">★</span>
                  <span className="wanted-star text-pink-500">★</span>
                  <span className="wanted-star text-pink-500">★</span>
                  <span className="wanted-star text-pink-500">★</span>
                  <span className="wanted-star text-pink-500">★</span>
                </div>
                <div className="text-gray-400 text-xs">REPUTATION</div>
              </div>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="relative z-10 flex flex-col items-center justify-center h-full text-center px-4">

          {/* Epic SAE Title */}
          <h1 
            ref={titleRef}
            className="gta-title text-9xl md:text-[12rem] lg:text-[16rem] mb-8"
            style={{
              color: '#ff0080',
              textShadow: `
                0 0 10px #ff0080,
                0 0 20px #ff0080,
                0 0 30px #ff0080,
                0 0 40px #ff0080,
                0 4px 0 rgba(0,0,0,0.9),
                0 8px 0 rgba(0,0,0,0.7),
                0 12px 0 rgba(0,0,0,0.5),
                0 16px 0 rgba(0,0,0,0.3),
                0 20px 30px rgba(0,0,0,0.8)
              `,
              transform: 'perspective(1000px) rotateX(10deg)'
            }}
          >
            SAE
          </h1>

          {/* Dynamic Subtitle */}
          <h2 
            ref={subtitleRef}
            className="font-street text-4xl md:text-6xl lg:text-7xl mb-8"
            style={{
              background: 'linear-gradient(45deg, #ff0080, #00ffff, #ff6600, #ff0080)',
              backgroundSize: '200% 200%',
              WebkitBackgroundClip: 'text',
              backgroundClip: 'text',
              color: 'transparent',
              animation: 'gradient-shift 3s ease infinite',
              textShadow: '0 0 30px rgba(255, 0, 128, 0.5)'
            }}
          >
            AUTO EMPIRE
          </h2>

          <p className="font-street text-2xl md:text-3xl text-white mb-12 max-w-5xl leading-relaxed"
             style={{ 
               textShadow: '2px 2px 4px rgba(0,0,0,0.8)',
               filter: 'drop-shadow(0 0 10px rgba(255, 255, 255, 0.3))'
             }}>
            {backgroundTexts[currentBg]}
          </p>

          {/* Vice City Style CTA Button */}
          <button 
            ref={buttonRef}
            onClick={scrollToNext}
            className="group relative px-16 py-6 text-2xl md:text-3xl font-street font-bold text-black overflow-hidden"
            style={{
              background: 'linear-gradient(45deg, #ff0080, #00ffff)',
              clipPath: 'polygon(20px 0%, 100% 0%, calc(100% - 20px) 100%, 0% 100%)',
              textShadow: 'none',
              boxShadow: `
                0 0 20px rgba(255, 0, 128, 0.6),
                0 0 40px rgba(0, 255, 255, 0.4),
                inset 0 0 20px rgba(255, 255, 255, 0.2)
              `
            }}
          >
            <span className="relative z-10 tracking-wider neon-flicker">JOIN THE EMPIRE</span>
            <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-pink-400 opacity-0 group-hover:opacity-50 transition-opacity duration-300"></div>
            <div className="absolute inset-0 bg-white opacity-0 group-hover:opacity-20 group-active:opacity-40 transition-opacity duration-150"></div>
          </button>
        </div>

        {/* Animated Progress Indicator */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 z-20">
          <div className="flex flex-col items-center">
            <div className="w-10 h-16 border-2 border-pink-500 rounded-full flex justify-center p-3 mb-3"
                 style={{ boxShadow: '0 0 20px rgba(255, 0, 128, 0.5)' }}>
              <div className="w-2 h-6 bg-gradient-to-b from-pink-500 to-cyan-500 rounded-full animate-bounce"></div>
            </div>
            <span className="font-street text-xs text-white uppercase tracking-wider neon-flicker">
              Enter Paradise
            </span>
          </div>
        </div>

        {/* Background Indicator */}
        <div className="absolute bottom-8 right-8 z-20">
          <div className="flex space-x-2">
            {backgrounds.map((_, index) => (
              <div
                key={index}
                className={`w-3 h-3 rounded-full transition-all duration-300 ${
                  index === currentBg 
                    ? 'bg-pink-500 shadow-lg' 
                    : 'bg-gray-600 hover:bg-gray-400'
                }`}
                style={index === currentBg ? {
                  boxShadow: '0 0 15px rgba(255, 0, 128, 0.8)'
                } : {}}
              />
            ))}
          </div>
        </div>

        {/* Vice City Vignette */}
        <div className="absolute inset-0 pointer-events-none"
             style={{
               background: 'radial-gradient(ellipse at center, transparent 30%, rgba(0,0,0,0.8) 100%)'
             }}
        />
      </section>

      {/* Add the gradient animation */}
      <style jsx>{`
        @keyframes gradient-shift {
          0% { background-position: 0% 50% }
          50% { background-position: 100% 50% }
          100% { background-position: 0% 50% }
        }
      `}</style>
    </>
  );
};

export default Hero;
